import React, { useContext } from 'react'
import TodoContext from '../../../todo-context/todo-context';
import TodoItem from './todoItem'
const todolist = () => {
  const {todos,toggleTodo,removeTodo} = useContext(TodoContext);
  return (
    <>
    todolist
    .........
    {
      todos.map(todo => {
        return <TodoItem 
        key={todo.id}
         {...todo}
         deleteButton={removeTodo}
          toggleButton={toggleTodo}
         />
      } )
    }
    </>
  )
}

export default todolist
