const todoReducer=(state, action)=>{
    switch(action.type){
        case 'ADD_TODO':
            return {
                todos: [...state.todos, action.payload]
            }
        case 'TOGGLE_TODO':
            return {}
        case 'REMOVE_TODO':
            return {}
        default:
            return state;
}
}
export default todoReducer;
