import { toJS } from 'mobx'
import {observer} from 'mobx-react'
import React,{useContext} from 'react'
import { useEffect } from 'react'
import RecipeReviewCard from '../components/cards'
import ProductContext from '../contexts/productContext/productContext';
import Pagination from '../components/pagination'
const home =observer( (props) => {
  const productContext = useContext(ProductContext)
  console.log(productContext.ProductStoreContext)
  console.log(toJS( productContext.ProductStoreContext.products))
  const {products}=toJS( productContext.ProductStoreContext)
  useEffect(()=>{
    async function fetchData(){
      const f = await productContext.ProductStoreContext.productsWithCount(perpage);
    }
    fetchData()
  },[])
const [currentPage, setCurrentPage] = React.useState(1)
const [perpage] = React.useState(5)
  return (
    <div>
      {products.map((product)=>{
        return <RecipeReviewCard key={product.id} {...product}/>
      })}
      <Pagination
         currentPage={currentPage}
          setCurrentPage={setCurrentPage}
      />
    </div>
  )
}
)
export default home
