import { observable } from "mobx";
import { observer } from "mobx-react-lite";
import React, { useContext, useState } from "react";
//calling context which include apis and mobx store
// import { StoreContext } from "../../context/authContext/auth.Context";
import AuthStore from '../../mobx/auth-mobx/Mobx.auth.Store'
const authComponet = observer(() => {
  // const { authStore } = useContext(StoreContext);
  // console.log(StoreContext);
  // const { authenticated,mobxLogin } = authStore;
  // console.log(authenticated,mobxLogin);
  const auth =AuthStore();
  console.log(auth);
  const { authenticated,mobxLogin } = auth;
  const handleSubmit = (event) => {
    // Prevent page reload
    event.preventDefault();
    //mobx ==> service=>context
    mobxLogin(errorMessages.uname, errorMessages.pass);
  };
  const [errorMessages, setErrorMessages] = useState({
    uname: "",
    pass: "",
  });
  // console.log(errorMessages);
  const handleChange = (event) => {
   setErrorMessages({
    ...errorMessages,
    [event.target.name] : event.target.value
   })
  }
  return (
    <div>
      <div className="form">
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <label>Username </label>
            <input
              type="text"
              name="uname"
            //   value={errorMessages.uname}
              onChange={(e)=>handleChange(e)}
            />
          </div>
          <div className="input-container">
            <label>Password </label>
            <input type="password" name="pass"  onChange={handleChange} />
          </div>
          <div className="button-container">
            <input type="submit" />
          </div>
        </form>
        {authenticated ? 'you are authenticated':'you are not authenticated'}
      </div>
    </div>
  );
});

export default authComponet;
