import { observable } from "mobx";
import { observer } from "mobx-react-lite";
import React, { useContext, useState } from "react";
//calling context which include apis and mobx store
import { StoreContext } from "../../context/authContext/auth.Context";

const authComponet = observer(() => {
  const { authStore } = useContext(StoreContext);
  const { authenticated } = authStore;
  console.log(authenticated);

  const handleSubmit = (event) => {
    // Prevent page reload
    event.preventDefault();
  };
  const [errorMessages, setErrorMessages] = useState({
    uname: "",
    pass: "",
  });
  const handleChange = (event) => {
    [event.target.name] = event.target.value;
  }
  return (
    <div>
      <div className="form">
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <label>Username </label>
            <input
              type="text"
              name="uname"
              value={errorMessages.uname}
              onChange={handleChange}
            />
          </div>
          <div className="input-container">
            <label>Password </label>
            <input type="password" name="pass" value={errorMessages.pass} />
          </div>
          <div className="button-container">
            <input type="submit" />
          </div>
        </form>
      </div>
    </div>
  );
});

export default authComponet;
