import React, { useCallback, useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  useHistory,
  useNavigate,
} from "react-router-dom";
import HomePage from "../../../mainEcomAppln/pages/home";
import PageNotFound from "../../../screening/notFound";
import Payment from "../../../mainEcomAppln/pages/payment";
import Cart from "../../../mainEcomAppln/pages/cart";
// import { createBrowserHistory } from "history";
import ResponsiveAppBar from "../../../components/appBar";
import { AppPaymentProvider } from "../../../store/paymentStore";
import SignIn from "../../../mainEcomAppln/pages/signIn";
const appRouter = () => {
  const history = useHistory();
  //  const navigate = useNavigate();
  //NB :Browser router should included in the root component(index.jsx);
  return (
    <>
      <Router history={history}>
        <AppPaymentProvider>
          <ResponsiveAppBar history={history} />
          <Switch>
            <Route exact path="/" component={HomePage} />
            <Route path="/payment" component={Payment} />
            <Route path="/cart" component={Cart} />
            <Route path="/signin" component={SignIn} />
            <Route children={<PageNotFound />} />
            {/* <Route path="about" render={() => <Redirect to="about-us" />} /> */}
          </Switch>
        </AppPaymentProvider>
      </Router>
    </>
  );
};
export default appRouter;
