import { Grid } from '@mui/material';
import { toJS } from 'mobx';
import React from 'react'
import { useContext } from 'react';
import ProductContext from '../contexts/productContext/productContext';

const categeries = () => {
  const productContext = useContext(ProductContext);
  console.log(productContext.ProductStoreContext);
  // console.log(toJS(productContext.ProductStoreContext.categeries),"ppp");
  console.log(toJS(productContext.ProductStoreContext.singleCategoryStore),"ppp");
  return (
    <div>
      categeries
      <Grid>
        {toJS(productContext.ProductStoreContext.categeries).map((item,index)=>(
          <>
          <h3>
            {item}
          </h3>
          </>
        ))}
      </Grid>

      <Grid container>
        {productContext.ProductStoreContext.categeries.map((item)=>(
          <>
            <Grid item xs={12} sm={6} md={5} lg={3} >
            <CategoryCard name={item} image="https://image.shutterstock.com/image-photo/category-word-scattered-english-alpabets-260nw-1594857565.jpg" 
             passingCardName ={getCategoryName}
            />
            </Grid>
          </>
        ))}
        </Grid>
      </div>
  )
}

export default categeries
