import { makeAutoObservable, toJS } from "mobx";

export class ProductStore {
  products = [];
  productApiwithLimit;
  singleProductApi=[];
  constructor(productApi,singleProductApi) {
    makeAutoObservable(this);
    this.productApiwithLimit = productApi;
    this.singleProductApi = singleProductApi;
  }
  productsWithCount = async (count) => {
    const c = await this.productApiwithLimit.AllProductsApi(count);
    this.products=c;
  };
  singleProductStore =async(id)=>{
    const r = await  this.singleProductApi.SingleProductApi(id);
    console.log(r,"single product");
    this.singleProductApi=r;
  }

}
