import {makeAutoObservable,toJS} from 'mobx';

export class ProductStore {
    products=[];
    productApiwithLimit
    constructor(productApi){
        makeAutoObservable(this)
        this.productApiwithLimit=productApi;
    }
    productsWithCount =async(count)=>{
       const c =  await this.productApiwithLimit.AllProductsApi(count);
       this.products.push({c})
     
       const uniqueIds = new Set();

const unique = this.products.filter(element => {
  const isDuplicate = uniqueIds.has(element.id);

  uniqueIds.add(element.id);

  if (!isDuplicate) {
    return true;
  }

  return false;
});
    }
}
