import { makeAutoObservable, toJS } from "mobx";

export class CartStore {
  carts = [];
  cartApi
  constructor(cartApi) {
   console.log(cartApi)
   this.cartApi =cartApi

    makeAutoObservable(this);
   
  }
  cartsWithCount = async (count) => {
    const carts = await this.cartApi.AllCartsApi(count);
    this.carts=carts
  };
  
  
}
