import {makeAutoObservable} from 'mobx'
export class CartStore {

    allCarts =[]
    cartsApi
    constructor(cartsApi){
        makeAutoObservable(this)
        this.cartsApi = cartsApi
    }
    cartItems =async(count)=>{
   const items = await this.cartsApi.AllCartsApi(count)
   this.allCarts=items
    }
}
