import React from "react";
import  AuthService  from "../../servieces/auth/authenticationServiece"; //Auth-api
import { AuthStore } from "../../mobx/auth-mobx/Mobx.auth.Store"; //mobx-auth-store



const authService = AuthService; //APIs
const authStore = new AuthStore(authService); // mobx-auth-store

export const StoreContext = React.createContext({
  authStore: authStore, //mobx-store
}); //mobx-auth-store

//1. componet called mobx . // componet
