import React, { useReducer } from 'react'
import { todoReducer } from './todo-reducer';
import TodoContext from './todo-context';
const MainContext = (props) => {
    const initialState = {
        todos: [],
    }

    const [state, dispatch]=useReducer(todoReducer, initialState);

    //add todo 
    const addTodo = (todo) => {
        dispatch({
            type: 'ADD_TODO',
            payload:todo
        })
    }
    //toggle todo
    const toggleTodo = (id) => {
        dispatch({
            type: 'TOGGLE_TODO',
            payload:id
        })
    }
    //remove todo
    const removeTodo = (id) => {
        dispatch({
            type: 'REMOVE_TODO',
            payload:id
        })
    }

  return (
    <> 
    MainContext
  <TodoContext.Provider value={{
        todos: state.todos,
        addTodo,
        toggleTodo,
        removeTodo
    }}>
        {props.children}
    </TodoContext.Provider>
    </>
  )
}

export default MainContext
