import React, { useCallback, useEffect } from "react";
import { BrowserRouter as Router, Route, Switch, useHistory } from "react-router-dom";
import HomePage from "../screening/home";
import pageNotFound from "../screening/notFound";
import Cart from "../screening/cartPage";
import Payment from "../screening/payment";
import { createBrowserHistory } from "history";
import ResponsiveAppBar from "../components/appBar";
const appRouter = () => {
  const history = useHistory();

  // console.log(useLocation().pathname);
  // const loc =useLocation().pathname;
  // const his = useHistory();
  // const his =createHistory({forceRefresh:true})

  const whichPage = useCallback((e,page) => {
    e.preventDefault();
    if (page == "Products") {
      history.push("/")
    //  window.location.reload();
    }
    if (page == "Cart") {
      history.push("/cart")
      //  console.log(loc);
    //  console.log(history.location.pathname);
    //  window.location.reload();
    }
    if (page == "Payments") {
      console.log("Payments");
      history.push("/payment")
    //  window.location.reload();
    }
  
  },[history]);
  
  return (
    <>
       <Router history={history}>
         <ResponsiveAppBar whichPage ={whichPage}/>
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route path="/payment" component={Payment} />
          <Route path="/cart" component={Cart} />
          <Route component={pageNotFound} />
        </Switch>
      </Router>
    </>
  );
};
export default appRouter;
