import React from 'react';
import {ProductStore} from  '../../stores/productStore'
import {ProductStoreWithLimit} from '../../servieces/product/allProductswithLimit'
import {SingleProductApi} from '../../servieces/product/singleProduct'

const authService = new ProductStoreWithLimit(); //API
const ProductStoreContext = new ProductStore(authService);

const singleProductService = new ProductStore();//API
const singleProductStoreContext = new SingleProductApi(singleProductService);

const ProductContext = React.createContext({
    ProductStoreContext,
    singleProductStoreContext
});

export default ProductContext;
