import { makeAutoObservable } from "mobx";
import { AuthService } from "../../servieces/auth/authenticationServiece";

export class AuthStore {
  authenticated = false;

  constructor(AuthService) {
    makeObservable(this, {
      authenticated: observable,
      setAuthenticated: action
  })
  }
  setAuthenticated=(authenticated)=> {
    this.authenticated = authenticated;
  }
//im doing compoent called mobx store and mobx store called api serviece
  async mobxLogin(username, password) {
    try {
      const tokenPayloadDto = await this.AuthService.loginApi(username, password);
      console.log(tokenPayloadDto);
      localStorage.setItem("access_token", tokenPayloadDto.access_token);
      this.setAuthenticated(true);
    } catch (err) {
      this.setAuthenticated(false);
    }
  }
 


  getAccessToken() {
    return localStorage.getItem("access_token");
  }

  isAuthenticated() {
    return this.authenticated;
  }
}
