import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Switch, useHistory } from "react-router-dom";
import HomePage from "../screening/home";
import pageNotFound from "../screening/notFound";
import Cart from "../screening/cartPage";
import Payment from "../screening/payment";
const appRouter = () => {
  useEffect(() => {
    console.log(useHistory().location.pathname);
  } , [useHistory().location.pathname]);
  return (
    <>
       <Router>
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route path="/payment" component={Payment} />
          <Route path="/cart" component={Cart} />
          <Route component={pageNotFound} />
        </Switch>
      </Router>
    </>
  );
};
export default appRouter;
